import React, { useState } from 'react';
import '../estilos/estilos_registro.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import Header from '../inicio/Header.js';
import Footer from '../inicio/Footer.js';

const Registro = () => {
  const [formData, setFormData] = useState({
    nombre: '',
    apellido: '',
    documento: 'CI',
    nodocumento: '',
    pais: '',
    provincia: '',
    ciudad: '',
    correo: '',
    celular: '',
    username: '',
    password: '',
  });

  const handleSubmit = (event) => {
    event.preventDefault();

    const {
      nombre,
      apellido,
      documento,
      nodocumento,
      pais,
      provincia,
      ciudad,
      correo,
      celular,
      username,
      password,
    } = formData;

    // Generar número de cuenta de 10 dígitos que inicie con 18
    const numeroCuenta = '18' + Math.floor(Math.random() * 9000000000 + 1000000000);

    // Datos para usuarios
    const usuarioData = {
      username: username,
      password: password,
    };

    // Datos para clientes
    const clienteData = {
      nombre: nombre,
      apellido: apellido,
      documento: documento,
      nodocumento: nodocumento,
      pais: pais,
      provincia: provincia,
      ciudad: ciudad,
      correo: correo,
      celular: celular,
      numeroCuenta: numeroCuenta,
    };

    // Enviar datos a la base de datos
    fetch('http://localhost:3030/save-data', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ usuario: usuarioData, cliente: clienteData }),
    })
    .then((response) => response.text())
    .then((data) => {
      alert(data);
      // Redirigir a otra página si es necesario
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div>
      <Header />
      <div id="registroForm">
        <h1>Registro</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="nombre">Nombre:</label>
            <input
              type="text"
              id="nombre"
              name="nombre"
              value={formData.nombre}
              onChange={handleChange}
              required
            />
            <br />
            <br />
          </div>
          <div>
            <label htmlFor="apellido">Apellido:</label>
            <input
              type="text"
              id="apellido"
              name="apellido"
              value={formData.apellido}
              onChange={handleChange}
              required
            />
            <br />
            <br />
          </div>
          <div>
            <label htmlFor="documento">Tipo de Documento:</label>
            <select
              id="documento"
              name="documento"
              value={formData.documento}
              onChange={handleChange}
            >
              <option value="CI">CI</option>
              <option value="Pasaporte">Pasaporte</option>
            </select>
            <br />
            <br />
            <label htmlFor="nodocumento">No de documento</label>
            <input
              type="text"
              id="nodocumento"
              name="nodocumento"
              value={formData.nodocumento}
              onChange={handleChange}
            />
            <br />
            <br />
          </div>
          <div>
            <label htmlFor="pais">País:</label>
            <select
              id="pais"
              name="pais"
              value={formData.pais}
              onChange={handleChange}
            >
              <option value="">Seleccione un país</option>
              {/* Agrega opciones de países según tu necesidad */}
            </select>
            <br />
            <br />
          </div>
          <div>
            <label htmlFor="provincia">Provincia:</label>
            <input
              type="text"
              id="provincia"
              name="provincia"
              value={formData.provincia}
              onChange={handleChange}
              required
            />
            <br />
            <br />
          </div>
          <div>
            <label htmlFor="ciudad">Ciudad:</label>
            <input
              type="text"
              id="ciudad"
              name="ciudad"
              value={formData.ciudad}
              onChange={handleChange}
              required
            />
            <br />
            <br />
          </div>
          <div>
            <label htmlFor="correo">Correo Electrónico:</label>
            <input
              type="email"
              id="correo"
              name="correo"
              value={formData.correo}
              onChange={handleChange}
              required
            />
            <br />
            <br />
          </div>
          <div>
            <label htmlFor="celular">Número de Celular:</label>
            <input
              type="tel"
              id="celular"
              name="celular"
              value={formData.celular}
              onChange={handleChange}
              required
            />
            <br />
            <br />
          </div>
          <div>
            <label htmlFor="username">Nombre de usuario:</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
            />
            <br />
            <br />
          </div>
          <div>
            <label htmlFor="password">Contraseña:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <br />
            <br />
          </div>
          <div className="submit-container">
            <input type="submit" value="Registrarse" id="Registrarse" />
          </div>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default Registro;
